package main

import (
	"testing"
)

func TestValid(t *testing.T) {
	var cases = []struct {
		s string
		e bool
	}{
		{"abc", true},
		{"aabbcc", true},
		{"aabbc", true},
		{"aabbcd", false},
		{`hfchdkkbfifgbgebfaahijchgeeeiagkadjfcbekbdaifchkjfejckbiiihegacfbchdihkgbkbddgaefhkdgccjejjaajgijdkd`, true},
		{``, true},
	}
	for _, c := range cases {
		g := valid(c.s)
		if g != c.e {
			t.Errorf("%q, Expected: %t, Got: %t", c.s, c.e, g)
		}
	}
}
